    def get_info(self):
        name     = self.name_input.text()
        phone    = self.phone_input.text()
        email    = self.email_input.text()
        bd       = self.bd_input.date().toPyDate()
        gender   = self.gender_input.currentText()
        scanType = self.smart_scan_for_input.currentText()
        hyperten = self.hyperten_input.currentText()
        underMed = self.under_med_input.currentText()
        infecteddays= self.days_since_infected_input.value()
        return [name,phone,email,str(bd),gender,scanType,hyperten,underMed,str(infecteddays)]


