    def predict_class(self,path,target_size,model):
        img = image_utils.load_img(path,target_size=target_size)
        # img.getpixel
        # plt.imshow(img)
        img = image_utils.img_to_array(img)
        img = img.reshape((1,) + img.shape)
        
        result = model.predict(img)
        if result[0][0] == 0:
            return 'benign'
        elif result[0][0] == 1:
            return 'malignant'


    def makecase(self):
        info = self.get_info()
        imageName= path.split("/")[-1]
        newpath=getcwd()+"/Cases/"+ info[0] #md5(' '.join(info).encode()).hexdigest()
        mkdir(newpath)
        copyfile(path,newpath+"/"+imageName)
        info.append(newpath+"/"+imageName)
        info.append(result)
        file=open(newpath+"/case.txt","w")
        for element in info:
            file.write(element+"\n")
        file.close()
        
        self.w = QWidget()
        ui=open_case_window()
        ui.setupUi(self.w,info)
        self.w.show()
        